package Nested;

public class Rangolinilesh {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int  a =8;a>=1;a--)
		
	//reversal		for (int  a =1;a<=8;a++)
		{
			for (int b =7;b>=a;b--)
			{	
				System.out.print(" ");
			}
			for (int e =1;e<=a;e++)
			{
			System.out.print("*");
			    }
			System.out.println(" ");
	
		 }

	}

}
